import React from 'react'
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableFooter from '@material-ui/core/TableFooter';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import TableHead from '@material-ui/core/TableHead';
import Container from '@material-ui/core/Container';
import Axios from 'axios'
import {EditAttributesRounded, DeleteForeverRounded} from '@material-ui/core' 
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';


// MATERIAL UI (Google) >> REACT HOOKS

class manageMovie extends React.Component{
    // state
    // lifecycle
    // function
    // render
    state = {
        data : []
    }
    componentDidMount(){
        this.getDataMovies()
    }
    getDataMovies = () => {
        Axios.get("http://localhost:2000/movies")
        .then((result)=>{
            
            this.setState({
                data : result.data 
            })
            
        })
        .catch((error) => {
            console.log(error)
        })
    }
    synopsisPrint = (text) => {
        var arr = text.split(" ")
        var synarr = []
        for(var i = 0; i<5; i++){
            synarr.push(arr[i])
        }
        return synarr.join(" ")
        
    }
    printMovieData = () => {
        var output = this.state.data.map((val,index) => {
            return(
                <TableRow>
                    <TableCell>{index+1}</TableCell>
                    <TableCell>{val.title}</TableCell>
                    <TableCell>{val.genre}</TableCell>
                    <TableCell><img src={val.image} height="50px" alt=""></img></TableCell>
                    <TableCell>{val.director}</TableCell>
                    <TableCell>{val.playingTime.join(",")}</TableCell>
                    <TableCell>{val.duration} Minutes</TableCell>
                    <TableCell>{this.synopsisPrint(val.synopsis)}...</TableCell>
                    <TableCell>
                        <div><input type="button" value="edit" className="btn btn-block btn-primary btn-sm"></input>
                        <input type="button" value="delete" className="btn btn-block btn-danger btn-sm"></input></div>
                    </TableCell>
                </TableRow>
            )
        })
        return output
    }
    render(){
        return(
            <div class="adminbackcolor">
                <Container fixed>
                    <h1 className="mt-3"><center>Manage Movie Page</center></h1>
                    <input type="button" value="Add Data" className="btn btn-success mb-2 mt-2"></input>
                    {/*MODAL START */}
                    {/*MODAL END */}
                    <Paper>
                        <Table>
                            <TableHead>
                                <TableCell>No</TableCell>
                                <TableCell>Title</TableCell>
                                <TableCell>Genre</TableCell>
                                <TableCell>Image</TableCell>
                                <TableCell>Director</TableCell>
                                <TableCell>Playtime</TableCell>
                                <TableCell>Duration</TableCell>
                                <TableCell>Synopsis</TableCell>
                                <TableCell>Action</TableCell>
                            </TableHead>
                            <TableBody>
                                {/* <TableRow>
                                    <TableCell></TableCell>
                                    <TableCell></TableCell>
                                    <TableCell></TableCell>
                                    <TableCell></TableCell>
                                    <TableCell></TableCell>
                                    <TableCell></TableCell>
                                    <TableCell></TableCell>
                                    <TableCell></TableCell>
                                    <TableCell></TableCell>
                                </TableRow> */}
                                {this.printMovieData()}
                               
                            </TableBody>
                        </Table>
                    </Paper>
                </Container>
            </div>
        )
    }
}

export default manageMovie;
