import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCouch } from '@fortawesome/free-solid-svg-icons'





class seatReservation extends React.Component{
    state = {
        seatSelected : [],
        color : "white"

        
    }
    

    onClickSeat = (id) => {
        var data = this.state.seatSelected
        data.push(id)
        this.setState({
            seatSelected : data
        })
        console.log(this.state.seatSelected)
    }
    printSeatReservation = () => {
        var alphabet = "ABCDEFGHIJ"
        var listalphabet = alphabet.split("")
        var output = listalphabet.map((val)=>{
            return(
                <div className="row justify-content-center filtercss">
                            <div className="row-md-1 d-inline filtercss">
                            {val}
                            </div>
                            <div className="row-md-3 d-inline ml-5">
                                 <FontAwesomeIcon className="seaticon" ref={val+"1"} color={this.state.color} size="2x" value={val+"1"} icon={faCouch} onClick={()=>this.onClickSeat(val+"1")}></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"2"} icon={faCouch} onClick={()=>this.onClickSeat(val+"2")}></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"3"} icon={faCouch} onClick={()=>this.onClickSeat(val+"3")}></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"4"} icon={faCouch} onClick={()=>this.onClickSeat(val+"4")}></FontAwesomeIcon>
                            </div>
                            <div className="row-md-5 d-inline mr-5 ml-5">
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"5"} icon={faCouch} onClick={()=>this.onClickSeat(val+"5")}></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"6"} icon={faCouch} onClick={()=>this.onClickSeat(val+"6")}></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"7"} icon={faCouch} onClick={()=>this.onClickSeat(val+"7")}></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"8"} icon={faCouch} onClick={()=>this.onClickSeat(val+"8")}></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"9"} icon={faCouch} onClick={()=>this.onClickSeat(val+"9")}></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"10"} icon={faCouch} onClick={()=>this.onClickSeat(val+"10")}></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"11"} icon={faCouch} onClick={()=>this.onClickSeat(val+"11")}></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"12"} icon={faCouch} onClick={()=>this.onClickSeat(val+"12")}></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"13"} icon={faCouch} onClick={()=>this.onClickSeat(val+"13")}></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"14"} icon={faCouch} onClick={()=>this.onClickSeat(val+"14")}></FontAwesomeIcon>
                            </div>
                            <div className="row-md-3 d-inline mr-5">
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"15"}icon={faCouch} icon={faCouch} onClick={()=>this.onClickSeat(val+"15")}>></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"16"}icon={faCouch} icon={faCouch} onClick={()=>this.onClickSeat(val+"16")}>></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"17"}icon={faCouch} icon={faCouch} onClick={()=>this.onClickSeat(val+"17")}>></FontAwesomeIcon>
                                 <FontAwesomeIcon className="seaticon" color="white" size="2x" value={val+"18"}icon={faCouch} icon={faCouch} onClick={()=>this.onClickSeat(val+"18")}>></FontAwesomeIcon>
                                 
                            </div>
                            
                        {console.log(this.refs.A1.value)}
                        </div>
                        
            )
        })
        return output
        
    }
    render(){
        return(
            <div className="mycontainer">
                <div className="container mt-5">
                    <div className="col mb-5">
                        <div className="filtercss row-md-4 text-center mb-5" style={{fontSize : "40px"}}>
                              Available Seats
                        </div>
                        {this.printSeatReservation()}
                    </div>
                    <input type="button" className="form-control btn-danger filtercss" value="PROCEED"></input>
                </div>
                
            </div>
        )
    }
}

export default seatReservation;
