import React from 'react'
import Axios from 'axios'
import { Link } from 'react-router-dom'


class movielist extends React.Component{
    state = {
        data : [],
        currentMovie : null
    }
    componentDidMount(){
        this.getDataMovies()
    }
    setCurrentMovie = (movienum) => {
        this.setState({
            currentMovie : movienum
        })
        console.log("Current Movie is " + this.state.currentMovie)
    }
    
    getDataMovies = () => {
        Axios.get("http://localhost:2000/movies")
        .then((result)=>{
            
            this.setState({
                data : result.data 
            })
            
        })
        .catch((error) => {
            console.log(error)
        })
    }
    printMovieData = () => {
        var output = this.state.data.map((val) => {
            return(
        
                    <div className="col-md-3 mycard mr-4 ml-4 mb-3 mt-3">
                        <img src={val.image} alt='MovieImage' width='100%' height="422px" />
                        {/* <div class="logo"> {val.id} </div> */}
                        <div className="mycardtext">
                        <div className="mb-2" > {val.title} </div>
                        <div className="mb-3" style={{fontSize : "15px", fontWeight : "bold"}}> ENGLISH </div>
                        <p> <input type="button" style={{fontSize : "15px", fontWeight : "bold", marginBottom : "4px"}}className="buttongenre" value={val.genre}/></p>
                        {/*
                        FORMAT 
                        <Link to={{
                        pathname: '/yourPage',
                        state: [{id: 1, name: 'Ford', color: 'red'}]
                        }}> Your Page </Link> 
                         */}
                         </div>
                        <Link to={"/moviedetail?id=" + val.id}>
                        <p>
                            <input type="button" style={{height : "45px", fontWeight: "bolder", fontSize : "18px"}}className="btn btn-danger btn-block" value="DETAILS" onClick={() => this.setCurrentMovie(val.id)} />
                        </p>
                        </Link>
                    </div>
                

            )
        })
        return output
    }
    render(){
        return (
            
            <div className='mycontainer pt-5'>
                <center><input type="text" className="form-control mt-3" placeholder="Input title to filter..." style={{width : "650px", alignSelf: "center"}}></input></center>
                
                <div className="row justify-content-center mt-4">
                {this.printMovieData()}

                
                </div>
                         
            </div>
        )
    }
}

export default movielist;

