import Axios from "axios";


// AXIOS GET ?

const INITIAL_STATE = { 
    currentUser : "",
    IS_LOGGED_IN : false,
    IS_ADMIN : false,
    saldo : 0,
}

var CountReducer = (state = INITIAL_STATE, action) =>{
    if(action.type === 'USER'){
        var saldouser= 0
        Axios.get('http://localhost:2000/users?username='+action.payload.username)
        .then((res)=>{
             saldouser = res.data[0].saldo
        })
        .catch((err)=>{
            console.log(err)
        })
        // GET SALDO 
        var userid = action.payload.username
        localStorage.setItem('username', userid)
        return{...state, IS_LOGGED_IN : true, IS_ADMIN : false, currentUser : userid, saldo : saldouser}
    }
    else if(action.type === 'ADMIN'){
        var saldouser= 0
        Axios.get('http://localhost:2000/users?username='+action.payload.username)
        .then((res)=>{
             saldouser = res.data[0].saldo
        })
        .catch((err)=>{
            console.log(err)
        })
        var userid = action.payload.username
        localStorage.setItem('username', userid)
        return{...state, IS_LOGGED_IN : true, IS_ADMIN : true, currentUser : userid, saldo : saldouser}
    }
    else if(action.type === "LOGOUT"){
        // localStorage.clear()
        localStorage.removeItem("username")
        return INITIAL_STATE
    }
    else {
        return state
    }
}


export default CountReducer;
