import React from 'react'
import Axios from 'axios'

class movielist extends React.Component{
    state = {
        data : []
    }
    componentDidMount(){
        this.getDataMovies()
    }
    getDataMovies = () => {
        Axios.get("http://localhost:2000/movies")
        .then((result)=>{
            
            this.setState({
                data : result.data 
            })
            
        })
        .catch((error) => {
            console.log(error)
        })
    }
    printMovieData = () => {
        var output = this.state.data.map((val) => {
            return(
        
                    <div className="col-md-3 mycard mr-3 ml-3">
                        <img src={val.image} alt='MovieImage' width='100%'/>
                        <p> {val.title} </p>
                        <p> English </p>
                        <p> <input type="button" className="buttongenre" value={val.genre}/></p>
                        <p><input type="button" className="btn btn-danger" value="showtime"/></p>
                    </div>
                

            )
        })
        return output
    }
    render(){
        return (
            <div className='container mt-5'>
                <div className="row">
                {this.printMovieData()}

                
                </div>
                         
            </div>
        )
    }
}

export default movielist;
