import React from 'react'
import {Paper} from '@material-ui/core'
import Axios from 'axios';


class Register extends React.Component{
    state={
        data : []
    }

    componentDidMount(){
        Axios.get("http://localhost:2000/users")
        .then((res)=> {
            this.setState({
                data : res.data
            })
        })
        .catch((err)=> {
            console.log(e)
        })
    }
    render(){
        return(
            <div className="container mt-5">
                <div className='row justify-content-center'>
                    <div className="col-md-8">
                        <Paper className="p-5">
                            <h1 className="mb-5">Register Form</h1>
                            <input type="text" className="form-control mt-3" placeholder="your username"></input>
                            <input type="text" className="form-control mt-3" placeholder="your password"></input>
                            <input type="text" className="form-control mt-3 mb-3 " placeholder="confirm password"></input>
                            <input type="button" className="btn btn-outline-success btn-lg mt-3" value="Register"></input>

                        </Paper>
                    </div>
                </div>
            </div>
        )
    }
}

export default Register;
