import React from 'react'
import Axios from 'axios'

class movielist extends React.Component{
    state = {
        data : []
    }
    componentDidMount(){
        this.getDataMovies()
    }
    getDataMovies = () => {
        Axios.get("http://localhost:2000/movies")
        .then((result)=>{
            this.setState({
                data : result.data
            })
        })
        .catch((error) => {
            console.log(error)
        })
    }
    render(){
        return (
            <div className='container'>
                <div className="row">
                    <div className="col-md-4">
                        <img src="https://media.21cineplex.com/webcontent/gallery/pictures/156074568118284_452x647.jpg" alt='MovieImage' width='50%'/>
                    </div>
                </div>               
            </div>
        )
    }
}

export default movielist;
