import React from 'react'
import Axios from 'axios'

class movielist extends React.Component{
    state = {
        data : []
    }
    componentDidMount(){
        this.getDataMovies()
    }
    getDataMovies = () => {
        Axios.get("http://localhost:2000/movies")
        .then((result)=>{
            
            this.setState({
                data : result.data 
            })
            
        })
        .catch((error) => {
            console.log(error)
        })
    }
    printMovieData = () => {
        var output = this.state.data.map((val) => {
            return(
        
                    <div className="col-md-3 mycard mr-3 ml-3 mb-3 ">
                        <img src={val.image} alt='MovieImage' height='100%'/>
                        <div class="logo"> {val.id} </div>
                        <div className="mb-2" style={{fontWeight : "bolder", fontFamily : "sans-serif", fontSize : "16px"}}> {val.title} </div>
                        <div className="mb-1" style={{fontSize : "11px", fontWeight : "bold"}}> English </div>
                        <p> <input type="button" className="buttongenre" value={val.genre}/></p>
                        <p><input type="button" className="btn btn-danger btn-block" value="SHOWTIMES"/></p>
                    </div>
                

            )
        })
        return output
    }
    render(){
        return (
            
            <div className='container mt-5 '>
                <h1>Movie List</h1>
                <div className="row justify-content-center">
                {this.printMovieData()}

                
                </div>
                         
            </div>
        )
    }
}

export default movielist;

