import Axios from 'axios'

export const logUser = (id) =>{
    function getSaldo(userid){
        Axios.get('http://localhost:2000/users?username='+userid)
            .then((res)=>{
                
                console.log(res.data[0].saldo)
                 return res.data[0].saldo
                
            }) 
            .catch((err)=>{
                return err
            })
    }
    return {
        type : "USER",
        payload : {username : id, saldo : getSaldo(id)}
    }
}

export const logAdmin = (id) =>{
    return {
        type : "ADMIN",
        payload : {username : id}
    }
}

export const UserLogOut = () => {
    return{
        type : "LOGOUT"
    }
}
