import React from 'react'
import {Paper} from '@material-ui/core'
import Axios from 'axios';
import {Redirect} from 'react-router-dom'


class Register extends React.Component{
    state={
        userdata : [],
        err : "",
        redirect : false
    }

    componentDidMount(){
        this.getLoginData()
    }
    getLoginData = () => {
    
        Axios.get('http://localhost:2000/users/')
        .then((res) => {
            
            this.setState({
                userdata : res.data
            })
            // console.log("ini userdata state", this.state.userdata)
            
        })
        .catch((err) => {
            console.log(err)
        })
    }

    registerIsValid = () => {
        var username = this.refs.reguser.value
        var password = this.refs.regpass.value
        var password2 = this.refs.regpass2.value

        if(username.replace(/\s/g, "") === "" || password.replace(/\s/g, "")=== "" || password2.replace(/\s/g, "") === ""){
            console.log("masuk")
            return(
            this.setState({
                err : "Please complete the empty field"
            })
            )
        }else {
            if(password !== password2){
                // tidak valid 
                return (
                this.setState({
                    err : "Your password and confirm password does not match"
                })
                )
            }else {
                Axios.get('http://localhost:2000/users?username='+username)
                .then((res)=>{
                    if(res.data.length > 0){
                        this.setState({
                            err : "Username is already being used"
                        })
                    }
                })
                .catch((err) =>{
                    console.log(err, "Masuk")
                    var userdata ={
                        role : "user",
                        username : username, 
                        password : password
                    }
                    Axios.post('http://localhost:2000/users',userdata)
                    .then((res)=>{
                        window.alert("Berhasil Register Akun")
                        this.setState({
                            redirect : true
                        })
                        
                    })
                    .catch((err)=>{

                    })
                })
            }
        }

        

        
        
       
        
    }
    render(){
        const { redirect } = this.state
        if(redirect) {
           return <Redirect to="/" push={true} />
        }
        return(
            <div className="container mt-5">
                <div className='row justify-content-center'>
                    <div className="col-md-8">
                        <Paper className="p-5">
                            <h1 className="mb-5">Register Form</h1>
                            <input type="text" ref="reguser" className="form-control mt-3" placeholder="your username"></input>
                            <input type="password" ref="regpass" className="form-control mt-3" placeholder="your password"></input>
                            <input type="password" ref="regpass2" className="form-control mt-3 mb-3 " placeholder="confirm password"></input>
                            <p className="mb-3" style={{color : "red", fontWeight : "bolder"}}> </p>
                            {
                            this.state.err === '' 
                            ?
                            <p></p>
                            :
                            this.state.err !== ''
                            ?
                            <div className='alert alert-danger'> {this.state.err} </div>
                            :
                            null
                            }
                            <input type="button" className="btn btn-outline-success btn-lg mt-3" value="Register" onClick={() => this.registerIsValid()}></input>

                        </Paper>
                    </div>
                </div>
            </div>
        )
    }
}

export default Register;
