import React from 'react';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem } from 'reactstrap';

import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
// import { UserLogOut } from './../redux/action/countAction'







// REACT HOOKS
class Header extends React.Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      isOpen: false
    };
  }
  componentDidMount =() => {
    console.log(this.props)
  }
  toggle() {
    this.setState({
      isOpen: !this.state.isOpen
    });
  }
  render() {
    return (
      <div>
        <Navbar color="dark"  expand="md">
          <NavbarBrand href="/" className="headername">Home</NavbarBrand>
          <NavbarToggler onClick={this.toggle} />
          <Collapse isOpen={this.state.isOpen} navbar>
            <Nav className="ml-auto " navbar>
            <UncontrolledDropdown className="headername" nav inNavbar>
                <DropdownToggle className="headername" nav caret>
                {this.props.currentUser !== "" && this.props.IS_LOGGED_IN === true
                ?
                "Welcome, " + this.props.currentUser 
                :
                this.props.IS_LOGGED_IN === false
                ?
                "You are currently not Logged in"
                :
                null
                }
                </DropdownToggle>
                {/*  */}
                <DropdownMenu right>
                  <DropdownItem>
                {this.props.currentUser !== "" && this.props.IS_LOGGED_IN === true
                ?
                <DropdownItem className="headername">
                  <Link to="/">
                    <p onClick={() => this.userlogout()}>Logout</p>
                    
                    {/* Function Here */}
                  </Link>
                </DropdownItem>
                :
                this.props.IS_LOGGED_IN === false
                ?
                <div>
                <DropdownItem className="headername">
                  <Link to="/pages/register">
                    Register
                  </Link>
                </DropdownItem>
                <DropdownItem className="headername">
                <Link to="/pages/loginPage">
                    Login
                  </Link>
                </DropdownItem>
                </div>
                :
                null
                }
                  </DropdownItem>
                </DropdownMenu>
              </UncontrolledDropdown>
              <NavItem>
                <NavLink href="/components/" className="headername">Components</NavLink>
              </NavItem>
              
              <NavItem >
                <Link to='/pages/loginPage'>
                <NavLink className="headername" style={{color : "white"}}>Login</NavLink>
                </Link>
              </NavItem>
            </Nav>
          </Collapse>
        </Navbar>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
    return{
       currentUser : state.CURRENT_USER_DATA.currentUser,
       IS_ADMIN : state.CURRENT_USER_DATA.IS_ADMIN,
       IS_LOGGED_IN : state.CURRENT_USER_DATA.IS_LOGGED_IN
       
    }
}

export default connect(mapStateToProps)(Header);
