import React from 'react'
import Axios from 'axios';

class moviedetail extends React.Component{
    state={
        data : []
    }

    componentDidMount(){
        var idmovie = this.props.location.search.replace("?id=", "") // Try this.props
        Axios.get("http://localhost:2000/movies/"+idmovie)
        .then((res)=>{
            this.setState({
                data : res.data
                
            })
            console.log(this.state.data)
            
        })
        .catch((err)=> {
            console.log(err)
        })
        
        
    }

    platingTimeRender = (val) => {
       console.log(val)
    }
    render(){
        //if(this.state.data == null){<p> Loading </p>}

        
        return(
            <div className="container mt-5 mb-5 p-0">
            {/* TO GET THE DATA PASS FROM THE LINK IN MOVIEDETAIL , THIS.PROPS.LOCATION.{VAR NAME} */}
           
                <div className="row">
                    <div className="col-md-4 p-4">
                        <img src={this.state.data.image} height="500px" alt=""></img>
                    </div>
                    <div className="col-md-8 p-5">
                        <h1>{this.state.data.title}</h1>
                        <p>Genre : {this.state.data.genre}</p>
                        <h5>Director : {this.state.data.director}</h5>
                        <p>{this.state.data.duration} Minutes </p>
                        <p>Playing At : {this.platingTimeRender(this.state.data.playingTime)}</p>
                        <p>{this.state.data.synopsis}</p>
                        <input type="button" className="btn btn-outline-success" value="buy ticket"></input>
                    </div>
                </div>
            </div>
        )
    }
}

export default moviedetail
