import React from 'react'
import { Table } from 'reactstrap'
import numeral from 'numeral'



class Transaction extends React.Component{
    state = {
        seat : [],
        moviename : [],
        price : 35000
    }
    componentDidMount(){
        var listseat = this.props.location.state.seatnumber
        var moviename = this.props.location.state.moviename
        console.log(moviename)
        this.setState({
            seat : listseat,
            moviename : moviename
        })
    }
    printCart = () => {
        var output = this.state.seat.map((value, index) => {
            return(
                <tr>
                    <td>{index+1}</td>
                    <td>{this.state.moviename}</td>
                    <td>{value}</td>
                    <td>{this.state.price}</td>
                </tr>
            )
        })
        return output
    }

    onCheckOut = () => {
        
    }
    render(){
        return(
            
                <div className="mycontainer">
                
                   <Table dark className="mt-5">
                 
                       <thead>
                            <tr>
                                <td>No</td>
                                <td>Movie Name</td>
                                <td>Seat Number</td>
                                <td>Price</td>
                            </tr>
                       </thead>
                       <tbody>
                            {this.printCart()}
                            <tr className="filtercss"> 
                                <td></td>
                                <td></td>
                                <td>Total Price : </td>
                                <td>{"Rp " + numeral(this.state.seat.length * this.state.price).format(0,0)}</td>
                            </tr>
                       </tbody>
                       <tfoot></tfoot>
                       
                   </Table>
                   <input type="button" className="form-control btn-lg btn btn-success filtercss" value="CHECKOUT"></input>
                   </div>
               
            
        )
    }
}

export default Transaction;
