import Axios from 'axios';

class moviedetail extends React.Component{
    state={
        data : []
    }

    componentDidMount(){
        var idmovie = this.props.location.search.replace("?id=", "") // Try this.props
        Axios.get("http://localhost:3000/moviedetail?id="+idmovie)
        .then((res)=>{
            this.setState({
                data : res.data
            })
            console.log(this.state.data)
        })
        .catch((err)=> {

        })
        
        
    }
    render(){
        return(
            <div className="container mt-5 mb-5 p-0">
            {/* TO GET THE DATA PASS FROM THE LINK IN MOVIEDETAIL , THIS.PROPS.LOCATION.{VAR NAME} */}
           
                <div className="row">
                    <div className="col-md-4 p-4">
                        <img src="https://roadshow.com.au/-/media/images/roadshow/movies/wb/2019/annabelle-comes-home/poster_237x354.jpg?mw=1224&hash=927A92A37BB5B1D166B845664A923925F1FF8C73&h=354&w=237&la=en" height="500px" alt=""></img>
                    </div>
                    <div className="col-md-8 p-5">
                        <h1>Movie Title</h1>
                        <p>Genre</p>
                        <h5>SUTRADARA</h5>
                        <p>Duration</p>
                        <p> Playing At</p>
                        <p>Synopsis</p>
                        <input type="button" className="btn btn-outline-success" value="buy ticket"></input>
                    </div>
                </div>
            </div>
        )
    }
}

export default moviedetail
