import React from 'react'
import { Table } from 'reactstrap'
import numeral from 'numeral'
import Axios from 'axios';
import {Redirect} from 'react-router'
import {connect} from 'react-redux'
import { Transaksi } from './../redux/actions/countActions'



class Transaction extends React.Component{
    state = {
        seat : [],
        moviename : [],
        price : 35000,
        redirect : false,
        cart : []
       
    }
    componentDidMount(){
        var listseat = this.props.location.state.seatnumber
        var moviename = this.props.location.state.moviename
        console.log(moviename)
        this.setState({
            seat : listseat,
            moviename : moviename
        })
        // AXIOS get & patch tambah isi cart
        Axios.get('http://localhost:2000/users?username='+this.props.currentUser)
        .then((res)=>{
            var arr = [res.data[0].cart]
            var cart = {
                movtitle : this.state.moviename,
                seat : this.state.seat,
                totalprice : this.state.seat.length * this.state.price

            }

            arr.push(cart)
            console.log("dapet user")
            console.log(cart)

            Axios.patch('http://localhost:2000/users/'+ res.data[0].id, {cart : cart})
            .then((res)=>{
                console.log("Berhasil")
            })
        })
        .catch((err)=>{
            console.log(err)
        })
    }
    printCart = () => {
        var output = this.state.cart.map((value, index) => {
            return(
                <tr>
                    <td>{index+1}</td>
                    <td>{value.movtitle}</td>
                    <td>{value.seat.join(",")}</td>
                    <td>{"Rp " + numeral(value.totalprice).format(0,0)}</td>
                </tr>
            )
        })
        return output
    }

    onCheckOut = () => {
        if(this.props.saldouser < (this.state.seat.length * this.state.price)){
            return window.alert("Saldo Belum Mencukupi, Top Up Terlebih Dahulu")
        }
        //post ke movie
        // var booked = this.props.location.state.booked (dari data)
        // booked.push([])
        // Axion.patch('http://localhost:2000/movies'+this.props.location.state.id, {booked : booked})
        //.then((res)=>{})
        //.catch((err)=>{})
        // console.log([...arr1, ...arr2]) arr1 + arr2 
   
        
        Axios.get('http://localhost:2000/movies?title='+this.state.moviename)
        .then((res)=> {
            var seat = this.state.seat
            console.log(res.data[0].booked)
            for(var i = 0; i< seat.length; i++){
                res.data[0].booked.push(seat[i])
            }
            console.log(res.data[0].booked)
           
            Axios.put('http://localhost:2000/movies/' + res.data[0].id, res.data[0])
            .then((result) => {
                
                console.log(result.data)
                Axios.get('http://localhost:2000/users?username='+this.props.currentUser)
                .then((rez)=>{
                    console.log("get user")
                    var transaction = {
                        movtitle : res.data[0].title ,
                        ticket : this.state.seat.length,
                        totalprice : this.state.seat.length * this.state.price
                    }
                    var restransaction = rez.data[0].transaction
                    restransaction.push(transaction)
                    rez.data[0].saldo = this.props.saldouser - (this.state.seat.length * this.state.price)
                    rez.data[0].transaction = restransaction

                    Axios.put('http://localhost:2000/users/'+ rez.data[0].id, rez.data[0])
                    .then((finalres)=>{
                        console.log("put user berhasil")
                        window.alert("Checkout Berhasil")
                        this.setState({
                            redirect : true
                        })
                        this.props.Transaksi(-(this.state.seat.length * this.state.price))
                        console.log(finalres)
                   })
                    .catch((err4)=>{
                        console.log(err4)
                    })
                })
                .catch((err3)=>{
                    console.log(err3)
                })
            })
            .catch((err2)=>{
                console.log(err2)
            })
        })
        .catch((err)=>{
            console.log(err)
        })
    }
    render(){
        if(this.state.redirect === true){
            return(
                <Redirect to="/"></Redirect>
            )
            
        }
        return(
            
                <div className="mycontainer">
                
                   <Table dark className="mt-5">
                 
                       <thead>
                            <tr>
                                <td>No</td>
                                <td>Movie Name</td>
                                <td>Seat Number</td>
                                <td>Price</td>
                            </tr>
                       </thead>
                       <tbody>
                            {this.printCart()}
                           
                            <tr className="filtercss text-success"> 
                                <td></td>
                                <td></td>
                                <td>Total Price : </td>
                                <td>{"Rp " + numeral(this.state.seat.length * this.state.price).format(0,0)}</td>
                            </tr>
                            <tr className="filtercss"> 
                                <td></td>
                                <td></td>
                                <td>Saldo Anda </td>
                                <td>{"Rp " + numeral(this.props.saldouser).format(0,0)}</td>
                            </tr>
                            
                       </tbody>
                       <tfoot></tfoot>
                       
                   </Table>
                   <input type="button" className="form-control btn-lg btn btn-success filtercss" value="CHECKOUT" onClick={()=>this.onCheckOut()}></input>
                   </div>
               
            
        )
    }
}

const mapStateToProps = (state) => {
    return{
       currentUser : state.CURRENT_USER_DATA.currentUser,
       IS_ADMIN : state.CURRENT_USER_DATA.IS_ADMIN,
       IS_LOGGED_IN : state.CURRENT_USER_DATA.IS_LOGGED_IN,
       saldouser : state.CURRENT_USER_DATA.saldo
    }
}
export default connect(mapStateToProps, {Transaksi})(Transaction);
