import React from 'react'
import {Paper} from '@material-ui/core'
import Axios from 'axios';


class Register extends React.Component{
    state={
        data : []
    }

    componentDidMount(){
        Axios.get("http://localhost:2000/users")
        .then((res)=> {
            this.setState({
                data : res.data
            })
            
        })
        .catch((err)=> {
            console.log(err)
        })
    }
    registerIsValid = () => {
        var username = this.refs.reguser.value
        var password = this.refs.regpass.value
        var password2 = this.refs.regpass2.value
        // if(this.state.data.username.indexOf(username) === -1){
        //     document.getElementsByTagName("p")[0].innerHTML = " Valid "
        // }else { 
        //     
        // }
       
        this.state.data.map((val) => {
            if(val.username === username){
                return document.getElementsByTagName("p")[0].innerHTML = " Tidak Valid "
            }
        })
        return document.getElementsByTagName("p")[0].innerHTML = " Valid "
        
    }
    render(){
        return(
            <div className="container mt-5">
                <div className='row justify-content-center'>
                    <div className="col-md-8">
                        <Paper className="p-5">
                            <h1 className="mb-5">Register Form</h1>
                            <input type="text" ref="reguser" className="form-control mt-3" placeholder="your username"></input>
                            <input type="password" ref="regpass" className="form-control mt-3" placeholder="your password"></input>
                            <input type="password" ref="regpass2" className="form-control mt-3 mb-3 " placeholder="confirm password"></input>
                            <p className="mb-3" style={{color : "red", fontWeight : "bolder"}}> </p>
                            <input type="button" className="btn btn-outline-success btn-lg mt-3" value="Register" onClick={() => this.registerIsValid()}></input>

                        </Paper>
                    </div>
                </div>
            </div>
        )
    }
}

export default Register;
